css:
.product-card section {
  border: 2px solid #ccc;
  padding: 10px
}

@media screen and (max-width: 480px) {
.product-card__buy.fade {
  opacity: 1 !important;
}}

.product-card__buy.fade {
  opacity: 1 !important;
}
.product-card__rating + .badge-dark, .product__rating + .product__out-of-stock {
font-size: 0px;
}
.product-card__rating + .badge-dark:before {
color: #fff;
background-color: #343a40;
content: 'Esgotado';
font-size: 1rem !important;
display: inline-block;
padding: .25em .4em;
font-weight: 700;
line-height: 1;
text-align: center;
white-space: nowrap;
vertical-align: baseline;
border-radius: .25rem;
}
.product__rating + .product__out-of-stock:before {
content: ‘Esgotado’;
font-weight: var(–font-bold);
font-size: var(–font-size-lg);
color: var(–warning);
}
.product__buy button {
color: #ffffff;
background-color: #5f9903;
border-color: #5f9903;
width: 100%;
}
.product__buy button:hover {
background: #4e7d02;
border-color: #4e7d02;
}
#product .prices__value {
    color: INFORME_A_COR_AQUI !important;
}
.payment-option__installment li small:nth-child(2) {
    color: red !important;
  }
.order-info__toggle {
    display: none !important;
}
@media(max-width: 767px) {.banners-grid.my-3.my-md-4.my-lg-5 div.col-12.col-lg-3.mb-3.mb-lg-0 {
    max-width: 50%;
    flex: 0 0 50%;
    padding-right: 10px;	
    padding-left: 10px;	
  }
